<template>
    <div>
        <div align="center">
            <img :src="'http://localhost:3000/ecommercegt/img?id=' + articulo._id" alt="Imagen del Articulo"
                style="max-width: 100%;">
        </div>
        <h4 style="margin-top: 20px;">{{ articulo.nombre }}</h4>
        <p>{{ articulo.descripcion }}</p>
        <h5>Categorias</h5>
        <ul class="list-group list-group-flush" v-for="cate in articulo.categoria" v-bind:key="cate">
            <li style="margin-left: 30px;">{{ cate }}</li>
        </ul>
        <h5 style="margin-top: 20px;"> Valor: Q. {{ articulo.precio }}</h5>
        <div class="row justify-content-between" style="margin-top: 20px;">
            <button v-if="$store.state.isAuthenticated && $store.state.role === 'PAQUETERIA'" @click="aceptarArticulo"
                class="btn btn-outline-success col" style="margin: 5px;">Aceptar Articulo</button>
        </div>
    </div>
</template>

<script>
import { toast } from 'vue3-toastify';
import 'vue3-toastify/dist/index.css';

export default {
    name: 'VerificarArticulo',
    data() {
        return {
            articulo: {
                _id: 'null',
                usuario: 'usuario',
                nombre: 'nombre',
                precio: 100.99,
                imagen: "data:image/png;base64,",
                descripcion: 'This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.',
                categoria: ['otros']
            }
        }
    },
    mounted() {
        let ver_obj = JSON.parse(localStorage.getItem('vuex'));
        let ver_auth = ver_obj.isAuthenticated;
        let ver_rol = ver_obj.role;//ADMIN USUARIO PAQUETERIA
        if (ver_auth && ver_rol === 'PAQUETERIA') {
            let vu = this;
            const id = this.$route.params.id;
            this.axios.get(`/api/articulo?_id=${id}`)
                .then(response => {
                    let data = response.data;
                    vu.articulo = data;
                })
                // eslint-disable-next-line no-unused-vars
                .catch(error => {
                    toast.error('No existe el producto introducido');
                    //Redireccion al inicio de la pagina
                    this.$router.push('/');
                });
        } else {
            this.$router.push('/');
        }
    },
    methods: {
        aceptarArticulo() {
            let id = this.articulo._id;
            let payload = {
                id: id,
                aceptado: true
            }
            this.axios.put('/api/articulo', payload)
                .then(response => {
                    console.log(response.data);
                    toast.success('Se acepto el articulo para la venta');
                    //Redireccion al inicio de la pagina
                    this.$router.push('/AceptarArticulo');
                })
                .catch(response => {
                    console.log(response.response.data.error);
                    toast.error(response.response.data.error)
                })
        }
    }
}
</script>

<style scoped>
img {
    height: 500px;
    align-self: center;
}
</style>